mapper.insert:
INSERT INTO search_history (
    user_id, keyword, create_time, is_deleted
) VALUES (
    #{userId}, #{keyword}, NOW(), 0
)

mapper.select:
SELECT * FROM search_history 
WHERE user_id = #{userId} 
AND is_deleted = 0
ORDER BY create_time DESC 
LIMIT #{limit}

mapper.update:
UPDATE search_history 
SET is_deleted = 1 
WHERE user_id = #{userId} 
AND id = #{id}

UPDATE search_history 
SET is_deleted = 1 
WHERE user_id = #{userId}

mapper.select:
SELECT * FROM search_history 
WHERE user_id = #{userId} 
AND keyword = #{keyword}
AND is_deleted = 0
LIMIT 1
